mapper.select:
SELECT COUNT(*) 
FROM FAV_BOARD 
WHERE BOARD_NO = #{boardNo} AND MEM_NO = #{memNo, jdbcType=VARCHAR}

SELECT COUNT(*) 
FROM FAV_BOARD 
WHERE BOARD_NO = #{boardNo}
  AND MEM_NO = #{memNo}

SELECT MEM_NO
FROM BOARD
WHERE BOARD_NO = #{boardNo}

mapper.update:
UPDATE BOARD 
SET BOARD_STATUS_CODE = 1
WHERE BOARD_NO = #{boardNo}
AND MEM_NO = #{memNo, jdbcType=VARCHAR}

UPDATE BOARD
SET BOARD_READ_COUNT = BOARD_READ_COUNT + 1
WHERE BOARD_NO = #{boardNo}
