mapper.update:
UPDATE MEMBER
SET 
  MEM_NAME = #{memName}, 
  MEM_TEL = #{memTel}, 
  MEM_EMAIL = #{memEmail}
WHERE MEM_NO = #{memNo}

UPDATE CORPORATE_MEMBER
SET CORP_MEM_DEPT = #{corpDepartment}
WHERE MEM_NO = #{memNo}

mapper.select:
SELECT MEM_PW
FROM MEMBER 
WHERE MEM_NO = #{memNo}

mapper.update:
UPDATE MEMBER
SET MEM_PW = #{memPw}
WHERE MEM_NO = #{memNo}
