mapper.update:
UPDATE MEMBER
SET 
    MEM_NAME = #{memName},
    MEM_TEL = #{memTel}
WHERE MEM_NO = #{memNo}

UPDATE CORP_MEM
SET 
  CORP_MEM_DEPT = #{corpMemDept}
WHERE MEM_NO = #{memNo}

mapper.select:
SELECT MEM_PW
FROM MEMBER 
WHERE MEM_NO = #{memNo}

mapper.update:
UPDATE MEMBER
SET MEM_PW = #{memPw}
WHERE MEM_NO = #{memNo}
