mapper.select:
SELECT COUNT(*)
FROM CV
WHERE CV_NO = #{cvNo}
  AND MEM_NO = #{memNo}

mapper.update:
UPDATE CV_AWARD
SET
    AWARD_NAME = #{awardName},
    AWARD_DATE = TO_DATE(#{awardDate}, 'YYYY-MM'),
    AWARD_ORG = #{awardOrg}
WHERE AWARD_ID = #{awardId}

UPDATE CV_QUALIFY
SET
    QUALIFY_NAME = #{qualifyName},
    QUALIFY_DATE = TO_DATE(#{qualifyDate}, 'YYYY-MM'),
    QUALIFY_ORG = #{qualifyOrg}
WHERE QUALIFY_ID = #{qualifyId}
