mapper.update:
UPDATE SUBSCRIPTION
SET MEM_GRADE = (
    SELECT MEM_GRADE
    FROM MEMBER_GRADE
    WHERE MEM_GRADE_NAME = #{orderName}
),
    SUB_UPDATE_AT = SYSDATE
WHERE MEM_NO = #{memNo}

UPDATE MEMBER
SET MEM_GRADE = (
  SELECT MEM_GRADE
  FROM MEMBER_GRADE
  WHERE MEM_GRADE_NAME = #{orderName}
)
WHERE MEM_NO = #{memNo}
