mapper.update:
UPDATE SUBSCRIPTION
SET MEM_GRADE = (
    SELECT MEM_GRADE
    FROM MEMBER_GRADE
    WHERE MEM_GRADE_NAME = #{orderName}
),
    SUB_END_AT = TRUNC(ADD_MONTHS(TRUNC(SYSDATE), 1) - 1),
    SUB_UPDATE_AT = SYSDATE,
    SUB_PAY_AT = TRUNC(SYSDATE),
    SUB_STATUS = #{status}
WHERE MEM_NO = #{memNo}

UPDATE MEMBER
SET MEM_GRADE = (
  SELECT MEM_GRADE
  FROM MEMBER_GRADE
  WHERE MEM_GRADE_NAME = #{orderName}
)
WHERE MEM_NO = #{memNo}

UPDATE "SUBSCRIPTION"
SET MEM_GRADE = (
      SELECT MEM_GRADE
      FROM "MEMBER"
      WHERE MEM_NO = #{memNo}
    ),
    SUB_STATUS = 0
WHERE MEM_NO = #{memNo}
