mapper.select:
SELECT RC.RECRUIT_CV_NO, RC.RECRUIT_CV_EDU, RC.RECRUIT_CV_CAREER, RC.RECRUIT_CV_PDF_TITLE, RC.RECRUIT_CV_DATE, RC.RECRUIT_CV_CHECK_FL, RC.MEM_NO, RC.RECRUIT_NO, R.RECRUIT_TITLE FROM RECRUIT_CV RC JOIN RECRUIT R ON RC.RECRUIT_NO = R.RECRUIT_NO JOIN CORP_MEM CM ON R.MEM_NO = CM.MEM_NO JOIN CORP_INFO CI ON CM.CORP_NO = CI.CORP_NO WHERE CI.CORP_NO = #{corpNo}
CI.CORP_NO = #{corpNo}
AND RC.RECRUIT_CV_EDU = #{recruitCVEdu}
AND RC.RECRUIT_CV_CAREER BETWEEN #{careerMin} AND #{careerMax}
ORDER BY R.RECRUIT_TITLE ASC

mapper.update:
UPDATE RECRUIT_CV
SET RECRUIT_CV_CHECK_FL = 'Y'
WHERE RECRUIT_CV_NO = #{cvNo}

mapper.delete:
DELETE FROM RECRUIT_CV
WHERE RECRUIT_CV_NO IN

	#{no}
